mapper.select:
select id, userid, imgurl, title, contents, categoryid, characters
FROM MATINGANIMAL
WHERE lower(title) like #{value}

select id, userid, imgurl, title, contents, categoryid, characters
FROM MATINGANIMAL
WHERE lower(contents) like #{value}

select id, userid, imgurl, title, contents, categoryid, characters
FROM MATINGANIMAL
WHERE lower(userid) like #{value}
